-- create "chats" table
CREATE TABLE `chats` (`id` bigint NOT NULL AUTO_INCREMENT, `chat_id` bigint NOT NULL, `type` varchar(255) NOT NULL DEFAULT '', `is_forum` bool NOT NULL DEFAULT false, `title` varchar(255) NOT NULL DEFAULT '', `user_name` varchar(255) NOT NULL DEFAULT '', `first_name` varchar(255) NOT NULL DEFAULT '', `last_name` varchar(255) NOT NULL DEFAULT '', `description` text NOT NULL, `create_at` datetime NOT NULL, `update_at` datetime NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `chat_chat_id` (`chat_id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "chat_options" table
CREATE TABLE `chat_options` (`id` bigint NOT NULL AUTO_INCREMENT, `chat_id` bigint NOT NULL, `key` varchar(50) NOT NULL, `value` varchar(255) NOT NULL DEFAULT '', `create_at` datetime NOT NULL, `update_at` datetime NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `chatoption_chat_id_key` (`chat_id`, `key`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "users" table
CREATE TABLE `users` (`id` bigint NOT NULL AUTO_INCREMENT, `user_id` bigint NOT NULL, `is_bot` bool NOT NULL DEFAULT false, `user_name` varchar(255) NOT NULL DEFAULT '', `first_name` varchar(255) NOT NULL DEFAULT '', `last_name` varchar(255) NOT NULL DEFAULT '', `create_at` datetime NOT NULL, `update_at` datetime NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `user_user_id` (`user_id`), INDEX `user_user_name` (`user_name`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
