-- create "game_accounts" table
CREATE TABLE `game_accounts` (`id` bigint NOT NULL AUTO_INCREMENT, `user_id` bigint NOT NULL, `account_id` varchar(30) NOT NULL, `game_token` varchar(100) NOT NULL DEFAULT '', `cookie_token` varchar(100) NOT NULL DEFAULT '', `stoken` varchar(255) NOT NULL DEFAULT '', `mid` varchar(50) NOT NULL DEFAULT '', `create_at` datetime NOT NULL, `update_at` datetime NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `gameaccount_user_id_account_id` (`user_id`, `account_id`), INDEX `gameaccount_account_id` (`account_id`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "game_roles" table
CREATE TABLE `game_roles` (`id` bigint NOT NULL AUTO_INCREMENT, `user_id` bigint NOT NULL, `account_id` varchar(30) NOT NULL, `role_id` varchar(30) NOT NULL, `level` bigint NOT NULL DEFAULT 0, `region` varchar(30) NOT NULL DEFAULT '', `region_name` varchar(30) NOT NULL DEFAULT '', `nick_name` varchar(100) NOT NULL DEFAULT '', `create_at` datetime NOT NULL, `update_at` datetime NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `gamerole_user_id_account_id_role_id` (`user_id`, `account_id`, `role_id`), INDEX `gamerole_account_id` (`account_id`), INDEX `gamerole_role_id` (`role_id`), INDEX `gamerole_region` (`region`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "game_role_attributes" table
CREATE TABLE `game_role_attributes` (`id` bigint NOT NULL AUTO_INCREMENT, `user_id` bigint NOT NULL, `account_id` varchar(30) NOT NULL, `role_id` varchar(30) NOT NULL, `name` varchar(100) NOT NULL, `type` bigint NOT NULL DEFAULT 0, `value` varchar(255) NOT NULL DEFAULT '', `create_at` datetime NOT NULL, `update_at` datetime NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `gameroleattribute_user_id_account_id_role_id_name` (`user_id`, `account_id`, `role_id`, `name`), INDEX `gameroleattribute_account_id` (`account_id`), INDEX `gameroleattribute_role_id` (`role_id`), INDEX `gameroleattribute_name` (`name`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
